const Wrapper = ({ children, theme = createTheme(defaultTheme) }) => (
    <MemoryRouter>
        <QueryClientProvider client={new QueryClient()}>
            <MuiThemeProvider theme={theme}>
                <AuthContext.Provider value={undefined as any}>
                    {children}
                </AuthContext.Provider>
                <Content />
            </MuiThemeProvider>
        </QueryClientProvider>
    </MemoryRouter>
);

